'use strict';
import React, {Component} from 'react';
import { Header, Icon, Image, Menu, Segment, Sidebar } from 'semantic-ui-react';


export default class sideNavMenu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      visible: true
    };
  };

  setVisible(){
    this.setState({
      visible: false
    });
  }

  render() {
    return (
      <Sidebar.Pushable as={Segment}>
        <Sidebar
          as={Menu}
          animation='overlay'
          icon='labeled'
          inverted
          onHide={(e) => this.setVisible()}
          vertical
          visible={true}
          width='thin'
        >
          <Menu.Item as='a'>
            <Icon name='home' />
            Home
          </Menu.Item>
          <Menu.Item as='a'>
            <Icon name='gamepad' />
            Games
          </Menu.Item>
          <Menu.Item as='a'>
            <Icon name='camera' />
            Channels
          </Menu.Item>
        </Sidebar>
        <Sidebar.Pusher>
          <Segment basic>
            <Header as='h3'>Application Content</Header>
            <Image src='/images/wireframe/paragraph.png' />
          </Segment>
        </Sidebar.Pusher>
    </Sidebar.Pushable>
    );
  }
}
