import React from 'react';
import { Card, Icon, Image, Header, Grid, Divider, Pagination } from 'semantic-ui-react'
import '../App.css';

import Anuska from '../static/Anuska.jpeg';
import Ashma from '../static/Ashma.jpeg';
import Haack from '../static/Haack.jpg';
import Pramila from '../static/Pramila.jpg';
import Rijan from '../static/Rijan.jpg';
import Sadikshaya from '../static/Sadikshya.jpg';
import Sarina from '../static/Sarina.jpeg';
import Sumit from '../static/Sumit.jpeg';



const OfficerLists = {
	President	: {title: 'President', name: 'Sarina Upreti', image: Sarina},
	VicePresident: {title: 'Vice President', name: 'Anuska Acharya', image: Anuska },
	Treasurer	: {title: 'Treasurer', name: 'Sadikshaya Adhikari', image: Sadikshaya},
	Secratary: {title: 'Secratary', name: 'Ashma Pradhan', image: Ashma },
	CommunityServiceManager: {title: 'Community Service Manager', name: 'Pramila Murray', image: Pramila },
	EventManager: {title: 'Event Manager', name: 'Rijan Timsina', image: Rijan },
	Advisor: {title: 'Advisor', name: 'Sumit Shrestha:', image: Sumit },
	FacultyAdvisor: {title: 'Faculty Advisor', name: 'Dr. Barry Haack:', image: Haack },
}

const OfficersCard = (props) => {
	const imgSrc = props.officer.image;
	const name = props.officer.name;
	const title = props.officer.title;
	const joinDate = 2019;
	const description = 'Description';

	return (
			<Card className = 'divCard'>
			    <Image src={imgSrc} size = 'medium'/>
			    <Card.Content>
			      <Card.Header as ='h2'>{name}</Card.Header>
			      <Card.Meta>
			      	<Card.Header><Header>{title}</Header></Card.Header>
			        <span className='date'>Joined in {joinDate}</span>
			      </Card.Meta>
			      <Card.Description>
			       {description}
			      </Card.Description>
			    </Card.Content>
			    <Card.Content extra>
			        <a href = 'http://www.facebook.com'><Icon name='facebook'/></a>
			        <a href = 'http://www.twitter.com'><Icon name='twitter' /></a>
			        <a href = 'http://www.instagram.com'><Icon name='instagram' /></a>
			        <a href = 'http://www.gmail.com'><Icon name='mail' /></a>
			    </Card.Content>
	  		</Card>
 )};

class Officers extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeOfficer: 'President'
    };
  };



  render() {
    return(
    	<div>
	    		<Card.Group centered itemsPerRow = {3}>
					<OfficersCard officer = {OfficerLists[this.state.activeOfficer]}/>
					<Divider vertical hidden/>
					<Pagination
				    defaultActivePage={1}
				    firstItem={null}
				    lastItem={null}
				    pointing
				    secondary
				    centered
				    totalPages={3}
			  		/>
		        </Card.Group>

	    </div>
  )};
};

export default Officers;
