import React from 'react';
import { Header, Icon, Image, Menu, Segment, Sidebar } from 'semantic-ui-react';


class NavigationBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      active: ''
    };
  };

  onClick(value){
    this.setState({
      active: value
    });
  };

  render() {
     return (
       <Menu color={'black'} inverted>
        <Menu.Item active={true}> 
          <Image src='./static/cover.png' size = 'icon'/>
        </Menu.Item>
        <Menu.Item
          name='Home'
          active={this.state.active == 'Home'}
          onClick = {(e, name) => this.onClick()}
        />
        <Menu.Item
          name='Menu 1'
          active={this.state.active == 'Menu 1'}
          onClick = {(e, name) => this.onClick()}
        />
        <Menu.Item
          name='Menu 2'
          active={this.state.active == 'Menu 2'}
          onClick = {(e, name) => this.onClick()}
        />
        <Menu.Item
          name='Menu 3'
          active={this.state.active == 'Menu 3'}
          onClick = {(e, name) => this.onClick()}
        />
        <Menu.Item
          name='Menu 4'
          active={this.state.active == 'Menu 4'}
          onClick = {(e, name) => this.onClick()}
        />
      </Menu>
  )};
};

export default NavigationBar;
