import React from 'react';
import { Grid, Divider } from 'semantic-ui-react';
import NavigationBar from './Sidebar.jsx';
import Home from './Home.jsx';
import About from './AboutNepal.jsx';
import Officers from './Officers.jsx';
import '../App.css';


const ContainerItem = props => {
  let activeItem = props.active
  alert(props.active)
  switch(activeItem) {
  case 'Home':
      return <Home/>
    break;
  case 'About':
    return <About/>
    break;
  case 'Officers':
    return <Officers/>
    break;
  default:
    return <Home />
}

}



class Wrapper extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      activeContainer: 'Home'
    };
  };

  handleMenuClick = (activeValue) => {
        this.setState({activeContainer: activeValue});
  }



  render() {
    return(
        <Grid>
          <Grid.Row>
            <Grid.Column width={3}>
              <NavigationBar activeContainer = {this.state.activeContainer}/>
            </Grid.Column>
            <Grid.Column width={13}>
              <Divider hidden/>
                <ContainerItem active = {this.state.activeContainer} handleClick = {this.handleMenuClick}/>
            </Grid.Column>
          </Grid.Row>
        </Grid>
  )};
};

export default Wrapper;
