import React from 'react';
import {Image, Menu, Icon, Sidebar } from 'semantic-ui-react';
import cover from '../static/cover.png';

const GraphTypeOptions = {
  'Item1': 'Home',
  'Item2': 'About Us',
  'Item3': 'Officers',
  'Item4': 'Events',
  'Item5': 'Photos',
  'Item6': 'Contact Us',
};

class NavigationBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      active: 'Home'
    };
  };

  handleItemClick(name) {
    console.log('clicked');
  };

  render() {
    const activeItem = this.state.active;
    const Item1 = GraphTypeOptions.Item1;
    const Item2 = GraphTypeOptions.Item2;
    const Item3 = GraphTypeOptions.Item3;
    const Item4 = GraphTypeOptions.Item4;
    const Item5 = GraphTypeOptions.Item5;
    const Item6 = GraphTypeOptions.Item6;

     return (
       <Sidebar as={Menu} icon='labeled' inverted
              vertical visible={true} width='thin'>
          <Menu.Item 
            name = {Item1}
            active={activeItem === Item1}
            onClick={(e) => this.handleClick()}>
            <Image src = {cover} />
          </Menu.Item>
          <Menu.Item name = {Item2}>
            <Icon name='group' />
            {Item1}
          </Menu.Item>
          <Menu.Item as='a' name = {Item3}>
            <Icon name='camera' />
            {Item3}
          </Menu.Item>
          <Menu.Item as='a'>
            <Icon name='calendar alternate' />
            {Item4}
          </Menu.Item>
          <Menu.Item as='a'>
            <Icon name='camera' />
            {Item5}
          </Menu.Item>
          <Menu.Item as='a'>
            <Icon name='phone square' />
            {Item6}
          </Menu.Item>
      </Sidebar>
  )};
};

export default NavigationBar;
